function formatBytes(bytes) {
	if (bytes === 0) return 0;
	var k = 100000,
	sizes = ['', '§f*10^5','§e*10^10','§6*10^15', '§d*10^20', '§d*10^25', '§4*10^30', '§d*10^35', '§d*10^40', colorcir('*10^45',3,1), colorcir('*10^50',3,1), colorcir('*10^55',3,1), colorcir('*10^60',3,1), colorcir('*10^65',3,1)],
	i = Math.floor(Math.log(bytes) / Math.log(k));
	if(sizes[i]){
		jjj=bytes / Math.pow(k, i);
		jjj=jjj.toFixed(jjj<10?2:(jjj<100?1:0));
		return jjj+sizes[i]
	}
	return bytes
}

function formatBytes3(a) {
	var n = a.split(",").map(Number);
	var ans = 0;
	for (var i = 0; i < n.length ; i++) {
		ans = ans * 256 + (n[i] & 0xFF);
	}
	if (n.length > 0 && (n[0] & 0x80)) {
		var power = 1;
		for (var j = 0; j < n.length; j++) {
			power *= 256;
		}
		ans -= power;
	}
	return ans;
}

function isBetween(min, max, val){
	return min <= val && val <= max
}
function castToUnsigned(n){
	if(n<0)
	{
		return 256+n
	}
	return n
}
function abcdefg(n){
aaaa=0;bbbb=1;k=0;j=1;
	for(i=n.length()-1;i>=0;i--){
		if(n[i]=="-")k=256-k;
		else {k+=n[i]*j;j*=10;}
		if(n[i-1]==","||i==0){aaaa+=k*bbbb;bbbb*=256;k=0;j=1;i--}
	}
	return aaaa
}
function standards(num) {
	var prod = '', count = 0;
	num = (num || 0).toString();
	for (var i = num.length - 1; i >= 0; i--) {
		count++;
		prod = num.charAt(i) + prod;
		if (!(count % 3) && i != 0) { prod = ',' + prod; }
	}
	return prod;
}
