	var Tier = 0;
	if (Itename[0]!='T'){Tier+=1;}
	if (Itename[0]=='T'){
	if (parseInt(Itename[2])==0){Tier+=10;}
		else Tier+=parseInt(Itename[1])+1;
	}
	return Tier
}

